type PcfPosn = ((Int, Int), (Int, Int)) 

pcfPosnToString :: PcfPosn -> String 
pcfPosnToString ((line1, col1), (line2, col2)) =
  if line1 == line2 
  then "[ " ++ Prelude.show line1 ++ ": " ++ Prelude.show col1 ++ "--" ++ Prelude.show col2 ++ " ]"
  else "[ " ++ Prelude.show line1 ++ ":" ++ Prelude.show col1 ++ " -- " ++ Prelude.show line2 ++ ":" ++ Prelude.show col2 ++ " ]"

data Token = ARROW PcfPosn
     	   | LPAREN PcfPosn
	   | RPAREN PcfPosn
	   | LPAIR PcfPosn
	   | RPAIR PcfPosn
	   | LBOX PcfPosn
	   | RBOX PcfPosn
	   | IDENT  PcfPosn String
	   | IDENTCAP PcfPosn String
	   | NUMBER PcfPosn Int
	   | NAT PcfPosn
	   | BOOL PcfPosn
	   | TRUE PcfPosn
	   | FALSE PcfPosn
	   | IF PcfPosn
	   | THEN PcfPosn
	   | ELSE PcfPosn
	   | FUNC PcfPosn
	   | PROJFST PcfPosn
	   | PROJSND PcfPosn
	   | INJFST PcfPosn
	   | INJSND PcfPosn
	   | CASE PcfPosn
	   | OF PcfPosn	   
	   | IN PcfPosn
	   | FIX PcfPosn  
	   | COLON PcfPosn
	   | SEMICOLON PcfPosn
	   | COMMA PcfPosn
	   | STAR PcfPosn
	   | PLUS PcfPosn
	   | EQUAL PcfPosn
	   | LEXERROR PcfPosn
	   | FCSTART
	   | FCEND
	   deriving  (Eq)

convertPosition :: AlexPosn -> String -> PcfPosn
convertPosition (AlexPn _ line col) s = ((line, col), (line, col + length s))

